create table "my_jdbc_river" ("_index" varchar(64),"_type" varchar(64),"_id" varchar(64),"source_timestamp" timestamp not null default current_timestamp,"source_operation" varchar(8) not null, "name" varchar(32), "amount" integer, "price" decimal(22,4), primary key ("_index", "_type", "_id", "source_timestamp", "source_operation"))
create table "my_jdbc_river_ack" ("_index" varchar(64),"_type" varchar(64),"_id" varchar(64),"target_timestamp" timestamp, "target_operation" varchar(64), "target_failed" boolean, "target_message" varchar(255), primary key ("_index", "_type", "_id"))
